mapper.select:
select * from d_file d,m_manufacture m where d.type='物料' or d.product_id=m.product_id and m.manufacture_procedure_tag='已完工'
and d.product_name like CONCAT('%',#{df.product_name},'%')
limit #{page.startRow},#{page.pageSize}

select * from s_gather where check_tag='审核通过' 

	and gather_id like CONCAT('%',#{sg.gather_id},'%')

limit #{page.startRow},#{page.pageSize}
